import React, { Component } from 'react';
import Board from './board';
import Container from 'react-bootstrap/Container';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Alert from 'react-bootstrap/Alert';



import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      values : { topLeft : ' ', topMiddle: ' ', topRight : ' ', 
                  middleLeft : ' ', middleMiddle : ' ', middleRight: ' ', 
                  bottomLeft : ' ', bottomMiddle : ' ', bottomRIght : ' '
      }, 
      turn : 0
    }
    this.toggleBoard = this.toggleBoard.bind(this)
    this.winningCondition = this.winningCondition.bind(this)
  }

  toggleBoard(position){

    this.setState({values :
                    {...this.state.values, 
                      [position] : this.state.turn % 2 === 0 ? 'X' : '0'
                    }, turn: this.state.turn + 1
                  })
    if (this.winningCondition() === true){

      console.log('winner')
    }
  }


  winningCondition() {
    if (this.state.values['topLeft'] === 'X' && this.state.values['topMiddle'] ==='X' && this.state.values['topRight'] === 'X'){
      return true
    }
    else {
      return false
    }
  }


  render() {
    return (
      <Container >

      <Jumbotron>
    <h1>Tic Tac Toe!</h1>
       <p>
    I've invented a new game and I've named it Tic Tac Toe! Press Play below to give it a try!
  </p>
  <p>
    <button variant="primary">Play Now!</button>
  </p>
</Jumbotron>
    <Board values={this.state.values} changeBoard = {this.toggleBoard} />

    <Alert>Hello</Alert>

    </Container>

    );
  }
}

export default App;
