import React, { Component } from 'react';
import Board from './board';
import Container from 'react-bootstrap/Container';
import Jumbotron from 'react-bootstrap/Jumbotron';

import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      players: {
        player1 : 'Adam',
        player2 : 'Not Adam'}, 
      rows : { 0 : { 0 : ' ', 1: ' ', 2: ' '}, 
                 1 : { 0 : ' ', 1: ' ', 2: ' '}, 
                 2 : { 0 : ' ', 1 : ' ', 2 : ' '}
      }, 
      turn : 0
    }
  }




  /* const toggleSquare = () => {
    this.setState()

  }*/
  render() {
    return (
      <Container >

      <Jumbotron>
    <h1>Tic Tac Toe!</h1>
       <p>
    I've invented a new game and I've named it Tic Tac Toe! Press Play below to give it a try!
  </p>
  <p>
    <button variant="primary">Play Now!</button>
  </p>
</Jumbotron>
    <Board values={this.state.rows} /></Container>

    );
  }
}

export default App;
