import React, { Component } from 'react';
import Board from './board';
import Container from 'react-bootstrap/Container';
import Jumbotron from 'react-bootstrap/Jumbotron';

import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      players: {
        player1 : 'Adam',
        player2 : 'Not Adam'}, 
      values : { topLeft : ' ', topMiddle: ' ', topRight : ' ', 
                  middleLeft : ' ', middleMiddle : ' ', middleRight: ' ', 
                  bottomLeft : ' ', bottomMiddle : ' ', bottomRIght : ' '
      }, 
      turn : 0
    }
    this.toggleBoard = this.toggleBoard.bind(this)
  }

  toggleBoard(props, position){
    this.setState({values :
                    {position : 'X'
                }})
  }


  /* const toggleSquare = () => {
    this.setState()

  }*/
  render() {
    return (
      <Container >

      <Jumbotron>
    <h1>Tic Tac Toe!</h1>
       <p>
    I've invented a new game and I've named it Tic Tac Toe! Press Play below to give it a try!
  </p>
  <p>
    <button variant="primary">Play Now!</button>
  </p>
</Jumbotron>
    <Board values={this.state.values} changeBoard = {this.toggleBoard} /></Container>

    );
  }
}

export default App;
